,p_plug_display_sequence=>10
,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    p.ID_PODROZY,',
'    p.NAZWA,',
'    p.DATA_WYJAZDU,',
'    p.DATA_POWROTU,',
'    p.KLIENT,',
'    p.LICZBA_OSOB,',
'    (',
'      NVL((SELECT SUM((e.DATA_WYJAZDU - e.DATA_PRZYJAZDU) * h.CENA_ZA_NOC)',
'           FROM ETAPY_PODROZY e',
'           JOIN HOTELE h ON e.ID_HOTELU = h.ID_HOTELU',
'           WHERE e.ID_PODROZY = p.ID_PODROZY), 0)',
'      +',
'      (NVL((SELECT SUM(a.CENA)',
'           FROM ZWIEDZONE_ATRAKCJE za',
'           JOIN ATRAKCJE_TURYSTYCZNE a ON za.ID_ATRAKCJI = a.ID_ATRAKCJI',
'           JOIN ETAPY_PODROZY ep ON za.NR_ETAPU = ep.NR_ETAPU',
'           WHERE ep.ID_PODROZY = p.ID_PODROZY), 0) * NVL(p.LICZBA_OSOB, 1))',
'      +',
'      NVL((SELECT SUM(r.KWOTA)',
'           FROM REZERWACJE r',
'           JOIN ETAPY_PODROZY ep ON r.ID_HOTELU = ep.ID_HOTELU',
'           WHERE ep.ID_PODROZY = p.ID_PODROZY), 0)',
'    ) AS PELNY_KOSZT',
